import React, { Component } from 'react';

class Operations extends Component {
    constructor() {
        super()
        this.state = {
            amount: null,
            vendor: "",
            category: ""
        }

    }

    deposit = () => {

        this.props.update(this.state)

    }
    Withdraw = () => {

        
        this.setState({
            amount : this.state.amount * -1
            
        },function(){
            this.props.update(this.state)

        })

    }


    updateAmount = (e) => {
        this.setState({
            amount: e.target.value
        })
    }
    updateVendor = (e) => {
        this.setState({
            vendor: e.target.value
        })
    }
    updateCategory = (e) => {
        this.setState({
            category: e.target.value
        })
    }

    render() {

        return (
            <div>
                <input placeholder="amount" value={this.state.amount} onChange={this.updateAmount}></input>
                <input placeholder="Vendor" value={this.state.vendor} onChange={this.updateVendor}></input>
                <input placeholder="Category" value={this.state.category} onChange={this.updateCategory}></input>
                <button value={this.state} onClick={this.deposit} >Deposit</button>
                <button value={this.state} onClick={this.Withdraw}>Withdraw</button>
            </div>

        )
    }


}


export default Operations
