}
updateAmount=(e)=>{
    this.setState({
        amount : e.target.value
    })
}
updateVendor=(e)=>{
    this.setState({
        vendor :e.target.value
    })
}
updateCategory=(e)=>{
    this.setState({
        Category :e.target.value
    })
}

    render() {

        return (
            <div>
                <input  placeholder="amount"value={this.state.amount} onChange={this.updateAmount}></input>
                <input  placeholder="V"value={this.state.vendor} onChange={this.updateVendor}></input>
                <input  placeholder="amount"value={this.state.Category} onChange={this.updateCategory}></input>
                <button  value={this.state} onClick={this.deposit} >Deposit</button>
                <button>Withdraw</button>
            </div>

        )
    }


}


export default Operations
