import React, { Component } from 'react';

class Operations extends Component {
    
    
deposit=(e)=>{
   this.setState({
       amount:e.target.value
   })
   console.log(this.state.amount);
   
   
}

    render() {
console.log(this.props.deposit);

        return (
            <div>
                <input  value={this.state.amount} ></input>
                <input  value={this.state.vendor}></input>
                <input  value={this.state.category}></input>
                <button multiple={true} value={[this.state.amount,this.state.vendor,this.state.category]} onClick={this.deposit} >Deposit</button>
                <button>Withdraw</button>
            </div>

        )
    }


}


export default Operations
