import React, { Component } from 'react';
import Operetions from './componenets/Opertions';
import Transactions from './componenets/transactions';
import Transaction from './componenets/transaction';
// import { BrowserRouter as Router, Route, Link } from 'react-router-dom';

const axios = require('axios');

class App extends Component {

  constructor() {
    super()
    this.state = {
      info: [
        { amount: 3200, vendor: "Elevation", category: "Salary" },
        { amount: -7, vendor: "Runescape", category: "Entertainment" },
        { amount: -20, vendor: "Subway", category: "Food" },
        { amount: -98, vendor: "La Baguetterie", category: "Food" }
      ],
      balance: 0
    }



  }

  async getTransactions() {
    return axios.get("http://localhost:8000/transactions")
  }

  updateInfo = (newInfo) => {
    const updatedInfo = [...this.state.info]
    axios.post("http://localhost:8000/transaction", newInfo)
    updatedInfo.push(newInfo)
    this.setState({
      info: updatedInfo
    })



  }


  delete = (id) => {
    axios.delete(`http://localhost:8000/transaction/${id}`)
    const info = this.state.info.filter(t => t._id !== id)


    this.setState({
      info: info
    })
  }



  deposit = (e) => {
    return console.log(e);

  }

  componentDidMount = async () => {
    const res = await this.getTransactions()

    let sum = 0
    for (let i = 0; i < this.state.info.length; i++) {
      sum += this.state.info[i].amount
    }
    this.setState({
      info: res.data,
      balance: sum
    })
  }




  render() {


    return (
      <div>
        <div>Balance:{this.state.balance}</div>
        <Operetions update={this.updateInfo} state={this.state.info} deposit={this.deposit} />
        <div className="App" >
          {this.state.info.map((i, index) => {
            return <Transactions delete={this.delete} key={i.vendor} info={i} id={index} />
          })}
        </div>

      </div >


    );
  }
}

export default App;
