deposit=(e)=>{
   console.log(e.target.value);
   
}

    render() {
console.log(this.props.deposit);

        return (
            <div>
                <input  value={this.state} onChange={this.deposit} ></input>
                <input placeholder="Vendor" value="Vendor"></input>
                <input placeholder="Category" value="Category"></input>
                <button multiple={true} value={['B', 'C']} >Deposit</button>
                <button>Withdraw</button>
            </div>

        )
